INSERT INTO `iwebshop_goods` VALUES (112, 'Chanel/香奈儿五号香水', 'SD00000000000112', 168, 1000.00, 1200.00, 990.00, '2011-05-27 13:41:33', '2012-05-27 13:41:35', '2011-05-27 13:43:07', 1000, 'upload/2011/05/27/20110527014456194.png', 0, '<p><img alt="" src="http://shop.jooyea.net/runtime/systemjs/editor/php/../attached/20110527134324_76107.png" border="0" /><img alt="" src="http://shop.jooyea.net/runtime/systemjs/editor/php/../attached/20110527134402_14207.png" border="0" /></p>\r\n<p align="left"><strong><span style="font-family:''宋体'';background:#ffffff none repeat scroll 0% 0%;font-size:14pt;">容量<span lang="en-us" xml:lang="en-us">/</span>市场价：</span></strong><span style="font-family:''宋体'';"><span style="background:#ffffff none repeat scroll 0% 0%;font-size:14pt;" lang="en-us" xml:lang="en-us">50ml/975</span><span style="background:#ffffff none repeat scroll 0% 0%;font-size:14pt;">元<span lang="en-us" xml:lang="en-us"> </span></span></span></p>\r\n<p></p>\r\n<p align="left"><strong><span style="font-family:''宋体'';background:#ffffff none repeat scroll 0% 0%;font-size:14pt;">香调<span lang="en-us" xml:lang="en-us">/</span>香型：</span></strong><span style="font-family:''宋体'';"><span style="background:#ffffff none repeat scroll 0% 0%;font-size:13.5pt;">醛花香调</span><span style="background:#ffffff none repeat scroll 0% 0%;font-size:14pt;" lang="en-us" xml:lang="en-us">/</span><span style="background:#ffffff none repeat scroll 0% 0%;font-size:14pt;">淡香水（<span lang="en-us" xml:lang="en-us">EDT</span>）</span></span></p>\r\n<p></p>\r\n<p align="left"><strong><span style="font-family:''宋体'';background:#ffffff none repeat scroll 0% 0%;font-size:14pt;">前味：</span></strong><span style="background:#ffffff none repeat scroll 0% 0%;font-size:14pt;"><span style="font-family:''宋体'';">格拉斯橙花、乙醛、香水树花</span><span lang="en-us" xml:lang="en-us"><br /></span><strong><span style="font-family:''宋体'';">中味：</span></strong><span style="font-family:''宋体'';">格拉斯茉莉、五月玫瑰</span><span lang="en-us" xml:lang="en-us"><br /></span><strong><span style="font-family:''宋体'';">后味：</span></strong><span style="font-family:''宋体'';">麦索尔檀木香、波旁香根草</span></span></p>\r\n<p></p>\r\n<p align="left"><strong><span style="font-family:''宋体'';background:#ffffff none repeat scroll 0% 0%;font-size:14pt;">产地<span lang="en-us" xml:lang="en-us">/</span>有效期：</span></strong><span style="background:#ffffff none repeat scroll 0% 0%;font-size:14pt;"><span style="font-family:''宋体'';">法国<span lang="en-us" xml:lang="en-us">/</span>五年</span></span></p>\r\n<p></p>\r\n<p align="left"><strong><span style="font-family:''宋体'';background:#ffffff none repeat scroll 0% 0%;font-size:14pt;">香水故事：</span></strong><span style="background:#ffffff none repeat scroll 0% 0%;font-size:14pt;" lang="en-us" xml:lang="en-us"><span style="font-family:''宋体'';">     </span></span></p>\r\n<p></p>\r\n<p align="left"><span style="background:#ffffff none repeat scroll 0% 0%;font-size:14pt;"><span style="font-family:''宋体'';">香奈儿五号<span lang="en-us" xml:lang="en-us">(CHANEL NO.5)</span>由法国香奈儿<span lang="en-us" xml:lang="en-us">(CHANEL)</span>公司于<span lang="en-us" xml:lang="en-us">1921</span>年推出。其香调主要由乙醛配合茉莉、五月玫瑰、印度薄荷、琥珀等多种名贵香料组成，清爽淡雅。它是世界上第一款加入乙醛的香水，属于醛香型香水的代表品牌。自<span lang="en-us" xml:lang="en-us">20</span>年代问世以来，一直声名卓著，历久不衰。而且，其瓶体造型简洁大方，成为香水包装的经典之一。</span><span lang="en-us" xml:lang="en-us"><br /><span style="font-family:''宋体'';">    </span></span><span style="font-family:''宋体'';">毋庸置疑，夏奈尔<span lang="en-us" xml:lang="en-us">5</span>号是世界上最著名的香水，夏奈尔<span lang="en-us" xml:lang="en-us">5</span>号的创始者恩尼斯<span lang="en-us" xml:lang="en-us">·</span>鲍与夏奈尔一起创造出了香水历史上的奇迹。</span><span lang="en-us" xml:lang="en-us"><br /><span style="font-family:''宋体'';">    </span></span><span style="font-family:''宋体'';">用香奈儿自己的话来说：<span lang="en-us" xml:lang="en-us">“</span>这就是我要的，一种截然不同于以往的香水，一种女人的香水。一种气味香浓，令人难忘的女人。<span lang="en-us" xml:lang="en-us">”</span></span></span></p>', '', '', NULL, 0.00, 20, '', 45, 0, 0, 99, 'upload/2011/05/27/20110527014456194_175_175.png', 'upload/2011/05/27/20110527014456194_100_100.png', 'a:2:{i:0;a:4:{s:2:"id";s:2:"83";s:5:"value";s:5:"50ml,";s:4:"type";s:1:"1";s:4:"name";s:6:"容量";}i:1;a:4:{s:2:"id";s:2:"75";s:5:"value";s:4:"XXL,";s:4:"type";s:1:"1";s:4:"name";s:6:"尺码";}}', 0);
